import React, { useState } from 'react';
import Grid from '@mui/material/Grid';
import Card from '@mui/material/Card';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import CardActions from '@mui/material/CardActions';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogActions from '@mui/material/DialogActions';

const animalData = [
  {
    name: 'Lizard',
    description: 'Lizards are a widespread group of squamate reptiles...',
    imageUrl: 'data:image/jpeg;base64,',
    moreInfo: 'Lizards have a unique ability to regenerate lost tails.',
  },
  {
    name: 'Elephant',
    description: 'Elephants are large mammals of the family Elephantidae...',
    imageUrl: 'data:image/jpeg;base64,',
    moreInfo: 'Elephants are known for their exceptional memory and intelligence.',
  },
  {
    name: 'Tiger',
    description: 'Tigers are the largest cat species...',
    imageUrl: 'data:image/jpeg;base64,',
    moreInfo: 'Tigers are excellent swimmers and often cool off in water.',
  },
];

export default function AnimalCards() {
  const [currentCardIndex, setCurrentCardIndex] = useState(0);
  const [open, setOpen] = useState(false);

  const handleNextCard = () => {
    setCurrentCardIndex((prevIndex) => (prevIndex + 1) % animalData.length);
  };

  const handlePrevCard = () => {
    setCurrentCardIndex((prevIndex) => (prevIndex - 1 + animalData.length) % animalData.length);
  };

  const handleMoreInfo = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const currentAnimal = animalData[currentCardIndex];

  return (
    <Grid
      container
      alignItems="center"
      justifyContent="center"
      style={{ minHeight: '100vh', position: 'relative', top: '-50px' }}
    >
      <Card sx={{ maxWidth: 345 }}>
        <CardMedia
          component="img"
          alt={currentAnimal.name}
          height="140"
          image={currentAnimal.imageUrl}
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="div">
            {currentAnimal.name}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            {currentAnimal.description}
          </Typography>
        </CardContent>
        <CardActions>
          <Button size="small" onClick={handlePrevCard}>
            Previous
          </Button>
          <Button size="small" onClick={handleNextCard}>
            Next
          </Button>
          <Button size="small" onClick={handleMoreInfo}>
            More Information
          </Button>
        </CardActions>
      </Card>

      {/* More Information Dialog */}
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>{currentAnimal.name} Information</DialogTitle>
        <DialogContent>
          <DialogContentText>
            {currentAnimal.moreInfo}
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} autoFocus>
            Close
          </Button>
        </DialogActions>
      </Dialog>
    </Grid>
  );
}
